import { useState } from 'react';
import { NavLink } from 'react-router-dom';

function Navbar({ size, resize }) {
  const [mobileOpen, setMobileOpen] = useState(false);

  const toggleSidebar = () => {
    resize(prev => (prev === 6 ? 25 : 6)); // expand/collapse
    setMobileOpen(prev => !prev); // toggle mobile visibility
  };

  return (
    <>
      {/* Hamburger button for mobile */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <i
          className="fa-solid fa-bars text-3xl text-white cursor-pointer"
          onClick={() => setMobileOpen(true)}
        ></i>
      </div>

      {/* Sidebar overlay for mobile */}
      <div
        className={`
          fixed inset-0 z-40 bg-black/50 transition-opacity duration-300
          ${mobileOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'}
          md:hidden
        `}
        onClick={() => setMobileOpen(false)}
      ></div>

      {/* Sidebar */}
      <div
        className={`
          nav-container
          fixed top-0 left-0 h-full bg-gray-900 text-white transition-transform duration-300
          ${mobileOpen ? 'translate-x-0' : '-translate-x-full'}
          md:translate-x-0 md:relative md:w-[${size}%]
        `}
        style={{ width: `${size}%` }}
      >
        <nav className="flex flex-col h-full">
          {/* Close button on mobile */}
          <div className="md:hidden flex justify-end p-4">
            <i
              className="fa-solid fa-xmark text-3xl cursor-pointer"
              onClick={() => setMobileOpen(false)}
            ></i>
          </div>

          {/* Desktop toggle */}
          <i
            className="fa-solid fa-bars text-3xl cursor-pointer hidden md:block mt-4 ml-3"
            onClick={toggleSidebar}
          ></i>

          {/* Links */}
          <ul className="pages mt-7 flex flex-col w-full">
            <li className="mb-5 w-full">
              <NavLink
                to="/"
                className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
              >
                <i className="fa-solid fa-house text-xl"></i>
                <span className="ml-5">{/* always show on desktop */}Home</span>
              </NavLink>
            </li>
            <li className="mb-5 w-full">
              <NavLink
                to="movie"
                className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
              >
                <i className="fa-solid fa-film text-xl"></i>
                <span className="ml-5">Movies</span>
              </NavLink>
            </li>
            <li className="mb-5 w-full">
              <NavLink
                to="Tv"
                className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
              >
                <i className="fa-solid fa-tv text-xl"></i>
                <span className="ml-5">Tv Series</span>
              </NavLink>
            </li>
          </ul>
        </nav>
      </div>
    </>
  );
}

export default Navbar;
