import { NavLink, useOutletContext, useParams } from "react-router-dom";
import { filterData } from "../store/BlogSlice";
const Blog = () => {
  const { id } = useParams();
  const { dispatch, blogs } = useOutletContext();
  const filteration = (id)=>{
    blogs.filter((el)=>{
      return el.id === id
    })

  }
  console.log(filteration)
  return (
    <>
      <div style={{"paddingTop":"3%"}}>
        <div className="Blog-Info">
        <h1>Exploring the Benefits of Serverless Architecture</h1>
        <p>Posted on: 12/24/2024 at 8:44:42 AM</p>
        <div className='card-tags'>
          <div className='tag'>#hgjhk</div>
          <div className='tag'>#hgjhk</div>
        </div>
        <hr/>
        <p className="para">Serverless architecture has gained immense popularity in recent years for its ability to simplify backend development and reduce operational overhead. This blog explains the key benefits of going serverless, including cost-efficiency, scalability, and faster time-to-market. We delve into popular serverless platforms like AWS Lambda, Azure Functions, and Google Cloud Functions, highlighting their unique features. Real-world use cases, such as building RESTful APIs, automating workflows, and processing real-time data, are explored in detail. Learn how serverless architecture empowers developers to focus on building features rather than managing infrastructure.</p>
        <hr/>
        <div className="BlogButtons">
          <NavLink className="btn1" to={`/EditBlog/${2}`}>Edit Blog</NavLink>
          <NavLink className="btn2">Delete Blog</NavLink>
        </div>
      </div>
      </div>
    </>
  )
}
export default Blog
