import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const getBlogs = createAsyncThunk('blog/getBlogs', async(_, thunkAPI) => {
    const {rejectWithValue} = thunkAPI
    try{
        const res = await fetch ("http://localhost:3005/blog")
        const data = await res.json()
        return data;

    }catch(error) {
        return rejectWithValue(error.message)
    }

})
export const insertBlog = createAsyncThunk("blog/insertBlog" , async (blogData , thunkAPI) => {
    const {rejectWithValue, getState} = thunkAPI
    try{
      console.log(blogData)
        const res = await fetch("http://localhost:3005/user", {
            method:'POST',
            body : JSON.stringify(blogData),
            headers: {
                'Content-type': 'application/json; charset=UTF-8'
            },
        })
        const data = await res.json()
        return data;

    }catch(error){
        return rejectWithValue(error.message)

    }
})


const BlogSlice = createSlice({
  name: "blog",
  initialState: {
    blogs: [],
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getBlogs.pending, (state) => {
        console.log("getBlogs pending")
      })
      .addCase(getBlogs.fulfilled, (state, action) => {
        state.blogs = action.payload;
        console.log("getBlogs fullfiled")
      })
      .addCase(getBlogs.rejected, (state, action) => {
        console.log("getBlogs rejected")
      })
  }
});

// export const { dataFilter, incrementQty, decrementQty,addUser,logIn } = ProductSlice.actions;
export default ProductSlice.reducer;
