import React, { useState, useMemo } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink } from "react-router-dom";

const Card = React.memo(({ toprated = [], trending = [], series = [], movies = [] }) => {
  console.log("Card.jsx render");

  const dataresult = useMemo(() => {
    const data = toprated.length > 0 ? toprated : trending;
    if (!data || data.length === 0) return [];

    const seriesIds = data[0].series_ids.map(String);
    const movieIds = data[0].movies_ids.map(String);

    const topSeries = series.filter(s => seriesIds.includes(String(s.id)));
    const topMovies = movies.filter(m => movieIds.includes(String(m.id)));

    return [...topSeries, ...topMovies];
  }, [toprated, trending, series, movies]);

  return (
    <Swiper slidesPerView={6} spaceBetween={13} pagination={false} navigation={false} modules={[Pagination, Navigation]}>
      {dataresult.length > 0 ? dataresult.map(el => {
        const isSeries = !!el.original_name;
        const linkType = isSeries ? "tv" : "movie";
        return (
          <SwiperSlide key={el.id}>
            <NavLink to={`/${linkType}/${el.id}`}>
              <img src={`${process.env.PUBLIC_URL}/images${el.poster_path}`} alt={el.original_name || el.original_title} />
              <h4>{el.original_name || el.original_title}</h4>
            </NavLink>
          </SwiperSlide>
        );
      }) : <p>No matching items found</p>}
    </Swiper>
  );
});

export default Card;
