import React from 'react'
import { useOutletContext } from 'react-router-dom'
function Show({id}) {
  const {movies} = useOutletContext()
  const datafilter =()=>{
    if(movies){
      return movies.find((el)=> el.id === id)
    }
  }
  console.log(datafilter())
  return (
    <div>
        <div
  className='show'
  style={{
    backgroundImage: `linear-gradient(to top, rgb(0, 0, 0), rgba(0, 0, 0, 0.98), rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.4)), url(https://image.tmdb.org/t/p/original//xUWUODKPIilQoFUzjHM6wKJkP3Y.jpg)`
  }}
>
        <div className='left-side'>
            <img src='https://image.tmdb.org/t/p/original//xUWUODKPIilQoFUzjHM6wKJkP3Y.jpg' alt=''></img>
        </div>
        <div className='right-side'>
            <h1>Frankenstein</h1>
            <div className='types'>
                <div className='type'>Drama</div>
                <div className='type'>Crime</div>
            </div>
            <p>Dr. Victor Frankenstein, a brilliant but egotistical scientist, brings a creature to life in a monstrous experiment that ultimately leads to the undoing of both the creator and his tragic creation.</p>
            
        </div>
    </div>
    </div>
  )
}

export default Show
