import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { incrementQty, decrementQty } from '../store/ProductSlice';

function Cart() {
  const cart = useSelector(state => state.product.cart);
  const dispatch = useDispatch();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className='about'>
      <h1 className="title">Cart</h1>
      <hr/>
      <div className='cart'>
        <div className='item-list'>
          <p>Item List</p>
          <hr/>
          {cart.map(item => (
            <div className='product-info' key={item.id}>
              <img src={item.image} alt={item.title} height={100} />
              <h5>{item.title}</h5>
              <div className="d-flex mb-4">
                <button className="btn px-3" onClick={() => dispatch(decrementQty(item.id))}>-</button>
                <p className="mx-3">{item.quantity}</p>
                <button className="btn px-3" onClick={() => dispatch(incrementQty(item.id))}>+</button>
              </div>
            </div>
          ))}
          <hr/>
        </div>

        <div className='order'>
          <p>Order Summary</p>
          <hr/>
          <div className='order-summary'>
            <div className='order-summary-row'>
              <h5>Products({cart.length})</h5>
              <h5>${total.toFixed(2)}</h5>
            </div>
          </div>
          <button>Go to checkout</button>
        </div>
        
      </div>
    </div>
  );
}

export default Cart;
