import { createSlice } from "@reduxjs/toolkit";

const ProductSlice = createSlice({
  name: "product",
  initialState: {
    products: [/* بيانات المنتجات هنا */],
    filteredProducts: [], // ✅ أضفنا حقل جديد لتخزين نتائج الفلترة
  },
  reducers: {
    viewData: (state) => {
      console.log(state.products);
    },

    dataFilter: (state, action) => {
      // فلترة المنتجات حسب التصنيف
      state.filteredProducts = state.products.filter(
        (el) => el.category === action.payload
      );

      // عرض النتيجة في console بشكل واضح
      console.log(JSON.parse(JSON.stringify(state.filteredProducts)));
    },
  },
});

export const { viewData, dataFilter } = ProductSlice.actions;
export default ProductSlice.reducer;
