import "./App.css";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Outlet } from 'react-router-dom';
import { useSelector ,useDispatch } from "react-redux";
import { useEffect } from "react";
import { getMovies, getSeries, getToprated } from "./store/MovieSlice";
function App() {
  const dispatch = useDispatch()
  useEffect(()=>{
    dispatch(getMovies())
    dispatch(getSeries())
    dispatch(getToprated())
  },[dispatch])
  const {movies , series} = useSelector((state)=>state.movietv)
  return (
    <div className="App">
      <Navbar/>
      <div className="main">
        <Outlet context={{dispatch , movies ,series}}/>
      </div>
      <Footer />
    </div>
  );
}

export default App;
