import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';

function Card({ toprated }) {
  const { series, movies } = useOutletContext();
  const [swiperRef, setSwiperRef] = useState(null);

  const datafilter = () => {
    if (Array.isArray(toprated) && toprated.length > 0) {
      const top = toprated[0]; // أول كائن في المصفوفة
      let result = [];

      if (series && series.length > 0 && Array.isArray(top.series_ids)) {
        const matchedSeries = series.filter((el) => top.series_ids.includes(el.id));
        result = [...result, ...matchedSeries];
      }

      if (movies && movies.length > 0 && Array.isArray(top.movies_ids)) {
        const matchedMovies = movies.filter((el) => top.movies_ids.includes(el.id));
        result = [...result, ...matchedMovies];
      }

      return result;
    }

    return [];
  };

  const dataresult = datafilter();

  return (
    <div className="card-list">
      {dataresult.length > 0 ? (
        dataresult.map((item) => (
          <div className="card" key={item.id}>
            <img
              src={`${process.env.PUBLIC_URL}/images${item.poster_path}`}
              alt={item.original_name || item.original_title}
            />
            <h3>{item.original_name || item.original_title}</h3>
          </div>
        ))
      ) : (
        <p>No matching items found.</p>
      )}
    </div>
  );
}

export default Card;
