import React from "react";
import { toComplete, deleteItem } from "../store/List";
import { useDispatch } from "react-redux";

function TodoItem({ items, selected }) {
  const dispatch = useDispatch();

  const checkHandler = (index) => {
    dispatch(toComplete(index));
  };

  const deleteHandler = (index) => {
    dispatch(deleteItem(index));
  };

  // ✅ Filter items based on boolean status
  var filteredItems = items.filter((item) => {
    return item.status === selected
  });
  console.log("filter",filteredItems)
  
  if (selected === "all"){
    filteredItems =items
  }
  console.log(filteredItems)

  return (
    <>
      {filteredItems.map((el, index) => (
        <div className="todo" key={index}>
          <li className={el.status ? "completed" : ""}>{el.title}</li>
          <button className="complete-btn" onClick={() => checkHandler(index)}>
            <i className="fas fa-check"></i>
          </button>
          <button className="trash-btn" onClick={() => deleteHandler(index)}>
            <i className="fas fa-trash"></i>
          </button>
        </div>
      ))}
    </>
  );
}

export default TodoItem;
