// Card.jsx
import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink} from "react-router-dom";
function Card({toprated ,series=[] ,movies=[]}) {
  const [swiperRef, setSwiperRef] = useState(null);


  const datafilter = (data ,toprated) => {
    if (Array.isArray(toprated) && toprated.length > 0) {
      const top = toprated[0]; // أول كائن في المصفوفة
      console.log(top)
      let result = [];

      if (data && data.length > 0 && Array.isArray(top.series_ids)) {
        const matchedSeries = data.filter((el) => top.series_ids.includes(el.id));
        result = [...result, ...matchedSeries];
      }
      return result;
    }
    else{return [];}
  };

  let dataresult = []
  if(series && series.length>0){
     dataresult = datafilter(series ,toprated)
  }
  if(movies && movies.length>0){
    dataresult = datafilter(movies ,toprated)
  }
  console.log("dataresult" , dataresult)

  return (
    <div className="Card">
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={6}
        spaceBetween={13}
        pagination={false}
        navigation={false}
        modules={[Pagination, Navigation]}
        className="mySwiper2"
      >
        {/* {toprated && toprated.length> 0 ? (toprated.map((el ,indx)=>{
          <SwiperSlide key={i} className="cardInfo">
            <NavLink className="imagehover" to="/"><img className="images" src={`${process.env.PUBLIC_URL}/images${el.poster_path}`}></img></NavLink>
            <h4>{el.}</h4>

          </SwiperSlide>
        })) :""} */}
      </Swiper>
    </div>
  );
}

export default Card;
