import React from 'react'

function Card({product}) {
  return (
    <div className='home-card'>
        {product.map((el,indx)=>(
          <div className='card-info' key={indx}>
            <img src="http://localhost:3000/assets/main.png.jpg" alt='Card'  height={200}></img>
            <p className='card-title'>{el.title}</p>
            <p>{el.description}</p>
            <p>{el.Price}</p>
        </div>
        ))}
        <div className='buttons card-btn'>
            <button>Buy Now</button>
            <button>Add to Cart</button>
        </div>
    </div>
  )
}

export default Card
