// Card.jsx
import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink } from "react-router-dom";

function Card({ toprated, trending ,  series = [], movies = [] }) {
  const [swiperRef, setSwiperRef] = useState(null);
  let types =""
  const datafilter = (data, toprated, type) => {
    if (Array.isArray(toprated) && toprated.length > 0) {
      const top = toprated[0];
      let result = [];
      // اختاري نوع الفلترة حسب نوع البيانات
      const idsKey = type === "series" ? "series_ids" : "movies_ids";
      const ids = top[idsKey];
      types = type === "series" ? "Tv" :"movie"
      if (data && data.length > 0 && Array.isArray(ids)) {
        result = data.filter((el) => ids.includes(el.id));
      }
      return result;
    }
    return [];
  };

  let dataresult = [];
  if (series && series.length > 0) {
    if(toprated && toprated.length>0 ){
      dataresult = datafilter(series, toprated, "series");
    }else{
      dataresult = datafilter(series, trending, "series");
    }
  } else if (movies && movies.length > 0) {
    if(toprated && toprated.length>0){
      dataresult = datafilter(movies, toprated, "movies");
    }else{
      dataresult = datafilter(movies, trending, "movies");
    }
  }



  return (
    <div className="Card">
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={6}
        spaceBetween={13}
        pagination={false}
        navigation={false}
        modules={[Pagination, Navigation]}
        className="mySwiper2"
      >
        {dataresult.length > 0 ? (
          dataresult.map((el) => (
            <SwiperSlide key={el.id} className="cardInfo">
              <NavLink className="imagehover" to={`/${types}/${el.id}`}>
                <img
                  className="images"
                  src={`${process.env.PUBLIC_URL}/images${el.poster_path}`}
                  alt={el.original_title || el.original_name}
                />
              </NavLink>
              <h4>{el.original_title || el.original_name}</h4>
            </SwiperSlide>
          ))
        ) : (
          <p className="no-data">No matching items found</p>
        )}
      </Swiper>
    </div>
  );
}

export default Card;
