import React, { useRef } from 'react';
import { NavLink, useParams, useSearchParams } from 'react-router-dom';

function List({ type, movies ,series }) {
  const datasearch = useRef("")
  const searchHandler = (data)=>{
    if(type === "tv"){
      series.filter((el)=>{
        return el.original_name === datasearch.current.value
      })

    }
    if(type === "movie"){
      movies.filter((el)=>{
        return el.original_title === datasearch.current.value
      })

    }
  }
  return (

    <div className='list-div'>
      {/* البحث */}
      <form className='search-part' onSubmit={(e)=>e.preventDefault()}>
        <input type='search' placeholder='Search movies' name='search'  ref={datasearch}/>
        <button className='search-button' onClick={()=>searchHandler(datasearch)}>
          <svg
            stroke='currentColor'
            fill='currentColor'
            strokeWidth='0'
            viewBox='0 0 16 16'
            height='1em'
            width='1em'
            xmlns='http://www.w3.org/2000/svg'
          >
            <path
              fillRule='evenodd'
              d='M15.7 13.3l-3.81-3.83A5.93 5.93 0 0 0 13 6c0-3.31-2.69-6-6-6S1 2.69 1 6s2.69 6 6 6c1.3 0 2.48-.41 3.47-1.11l3.83 3.81c.19.2.45.3.7.3.25 0 .52-.09.7-.3a.996.996 0 0 0 0-1.41v.01zM7 10.7c-2.59 0-4.7-2.11-4.7-4.7 0-2.59 2.11-4.7 4.7-4.7 2.59 0 4.7 2.11 4.7 4.7 0 2.59-2.11 4.7-4.7 4.7z'
            />
          </svg>
        </button>
      </form>

      {/* قائمة الأفلام */}
      <div className='list-items'>
        {movies && movies.length > 0 ? (
          movies.map((el) => {
            // مسار الصورة النهائي
            const imageUrl = `${process.env.PUBLIC_URL}/images${el.poster_path}`;
            return (
              <div className='list-card' key={el.id}>
                <NavLink to={`/${type}/${el.id}`}>
                  <img src={imageUrl} alt={el.original_title} />
                  <h4>{el.original_title}</h4>
                </NavLink>
              </div>
            );
          }) 
        ) : (
          series && series.length>0 ?(
            series.map((el) => {
            // مسار الصورة النهائي
            const imageUrl = `${process.env.PUBLIC_URL}/images${el.poster_path}`;
            return (
              <div className='list-card' key={el.id}>
                <NavLink to={`/${type}/${el.id}`}>
                  <img src={imageUrl} alt={el.original_name} />
                  <h4>{el.original_name}</h4>
                </NavLink>
              </div>
            );
          })
          ) :""
        )}
      </div>
    </div>
  );
}

export default List;
