import "./App.css";
import Form from "./components/Form";
import TodoList from "./components/TodoList";
import { useSelector }  from "react-redux";
function App() {
  const {list} = useSelector((state)=>state.list)
  const [selected, setSelected] = useState("all");
  const handleFilterChange = (e) => {
    setSelected(e.target.value); // ✅ updates state correctly
  };
  return (
    <div className="App">
      <header>
        <h1>To-Do List</h1>
      </header>
      <Form list={list} selected={selected}/>
      <TodoList list={list} selected={selected}/>
    </div>
  );
}

export default App;
