import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { incrementQty, decrementQty } from '../store/ProductSlice';
import { getCart } from '../store/ProductSlice';
function Cart() {
  const cart = useSelector(state => state.product.cart);
  const login = useSelector(state => state.product.login);
  const dispatch = useDispatch();

  // Filter cart items for the current user
  const userCart = cart.filter(item => item.userId === login.userId);

  const total = userCart.reduce((sum, item) => sum + item.price * (item.quantity || 1), 0);

  return (
    <div className='about'>
      <h1 className="title">Cart</h1>
      <hr/>
      <div className='cart'>
        <div className='item-list'>
          <p>Item List</p>
          <hr/>
          {userCart.length > 0 ? (
            userCart.map(item => (
              <div className='product-info' key={item.id}>
                <img src={item.image} alt={item.title} height={100} />
                <h5>{item.title}</h5>
                <div className="d-flex mb-4">
                  <button className="btn px-3" onClick={() => dispatch(decrementQty(item.id))}>-</button>
                  <p className="mx-3">{item.quantity || 1}</p>
                  <button className="btn px-3" onClick={() => dispatch(incrementQty(item.id))}>+</button>
                </div>
              </div>
            ))
          ) : (
            "There are no products yet!!"
          )}
          <hr/>
        </div>

        <div className='order'>
          <p>Order Summary</p>
          <hr/>
          <div className='order-summary'>
            <div className='order-summary-row'>
              <h5>Products({userCart.length})</h5>
              <h5>${total.toFixed(2)}</h5>
            </div>
          </div>
          <button>Go to checkout</button>
        </div>
      </div>
    </div>
  );
}


export default Cart;
