import { createSlice } from "@reduxjs/toolkit";

const ListSlice = createSlice({
  name: "list",
  initialState: {
    list: [
      { title: "yafa", status: "uncompleted" },
      { title: "yara", status: "uncompleted" },
    ],
  },
  reducers: {
    insert: (state, action) => {
      state.list.push(action.payload); // ✅ Corrected
    },
    toComplete: (state, action) => {
      state.list[action.payload].status = "completed"; // ✅ OK
    },
  },
});

export const { insert, toComplete } = ListSlice.actions;
export default ListSlice.reducer;
