import React, { useRef, useState, useEffect } from "react";
import { useOutletContext } from "react-router-dom";
import Cards from "../components/Cards";
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Autoplay, Pagination, Navigation } from 'swiper/modules';
const Home = () => {
  const progressCircle = useRef(null);
  const progressContent = useRef(null);
  const [swiperRef, setSwiperRef] = useState(null);
    const {series , movies , toprated ,trending} = useOutletContext()
       const getTopRatedItems = () => {
    if (!toprated || toprated.length === 0) return [];

    const { series_ids = [], movies_ids = [] } = toprated[0];

    const topSeries = Array.isArray(series)
      ? series.filter((s) => series_ids.includes(s.id))
      : [];
    const topMovies = Array.isArray(movies)
      ? movies.filter((m) => movies_ids.includes(m.id))
      : [];

    // دمج النتيجتين في مصفوفة واحدة
    return [...topSeries, ...topMovies];
  };

  const topItems = getTopRatedItems();
  return (
    <div className="home">
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 5500,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={false}
        modules={[Autoplay, Pagination, Navigation]}
        // onAutoplayTimeLeft={onAutoplayTimeLeft}
        className="mySwiper"
      >
      {topItems && topItems.length>0?Autoplay(
        topItems.map((el)=>{
          <SwiperSlide>
          <div className="SwiperContant">
            <div className="SwiperInfo">
              <h2 className="Title">{el.original_name}</h2>
              <p className="desc">{el.overview}</p>
            </div>
            <div className="SwiperImage">

            </div>
          </div>
        </SwiperSlide>

        })
      ):""}

        <div className="autoplay-progress" slot="container-end">
          <svg viewBox="0 0 48 48" ref={progressCircle}>
            <circle cx="24" cy="24" r="20"></circle>
          </svg>
          <span ref={progressContent}></span>
        </div>
      </Swiper>
      <div className="sections">
      <div className="section">
        <Cards title={"Trending movies"} trending={trending}  movies={movies}/>
      </div>
      <div className="section">
        <Cards title={"Top rated movies"}  toprated={toprated} series={series}/>
      </div>
      <div className="section">
        <Cards title={"Trending series"} trending={trending} series={series}/>
      </div>
      <div className="section">
        <Cards title={"Top rated Series"} toprated={toprated}  movies={movies}/>
      </div>
      </div>
      
    </div>
  );
};

export default Home;
