import { createSlice } from "@reduxjs/toolkit";

const ListSlice = createSlice({
    name : "list",
    initialState : {list: [{title:"yafa",status:"uncompleted"},{title:"yara",status:"uncompleted"}]},
    reducers:{
        insert:(state,action)=>{
            state.push(action.payload)
        },
        toComplete:(state,action)=>{
            state.list[action.payload].status=completed

        }
    }
})
export const {insert,toComplete} = ListSlice.actions
export default ListSlice.reducer
