import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// جلب البيانات من السيرفر
export const fetchProduct = createAsyncThunk('product/fetchProduct', async (_, thunkAPI) => {
    const response = await fetch("http://localhost:3005/product");
    const data = await response.json(); // تحويل response إلى JSON
    console.log(data)
    return data;
  }
);

const ProductSlice = createSlice({
  name: "product",
  initialState: {
    products: [],
    filteredProducts: [],
    cart: [],
    loading: false,
    error: null
  },
  reducers: {
    dataFilter: (state, action) => {
      if (action.payload === "All") {
        state.filteredProducts = state.products;
      } else {
        state.filteredProducts = state.products.filter(
          (el) => el.category === action.payload
        );
      }
    },
    addToCart: (state, action) => {
      const product = state.products.find(el => el.id === action.payload);
      if (product) {
        const exist = state.cart.find(el => el.id === product.id);
        if (exist) {
          exist.quantity += 1;
        } else {
          state.cart.push({ ...product, quantity: 1 });
        }
      }
    },
    incrementQty: (state, action) => {
      const item = state.cart.find(el => el.id === action.payload);
      if (item) item.quantity += 1;
    },
    decrementQty: (state, action) => {
      const item = state.cart.find(el => el.id === action.payload);
      if (item && item.quantity > 1) item.quantity -= 1;
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchProduct.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchProduct.fulfilled, (state, action) => {
        state.loading = false;
        state.products = action.payload;
        state.filteredProducts = action.payload; // نعرض كل المنتجات كبداية
        console.log(state.products)
      })
      .addCase(fetchProduct.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  }
});

export const { dataFilter, addToCart, incrementQty, decrementQty } = ProductSlice.actions;
export default ProductSlice.reducer;
