import { useState } from "react";
import "./App.css";
import Form from "./components/Form";
import TodoList from "./components/TodoList";
import { useSelector }  from "react-redux";

function App() {
  const {list} = useSelector((state)=>state.list)
  const [selectItem ,setSelectItem] = useState("")

   const selected = (selectedStatus)=>{
    const selects = selectedStatus
    if (selects === "completed"){
      setSelectItem("true")
    }
    if (selects === "uncompleted"){
      setSelectItem("false")
    }
    if (selects === "all"){
      setSelectItem("all")
    }
  }
  return (
    <div className="App">
      <header>
        <h1>To-Do List</h1>
      </header>
      <Form list={list} selected={selected}/>
      <TodoList list={list} selected={selectItem}/>
    </div>
  );
}

export default App;
