import React, { useRef } from "react";
import { useOutletContext } from "react-router-dom";
import BlogCard from "../components/BlogCard";
const Home = () => {
  const { dispatch, blogs } = useOutletContext();
  const searchInput = useRef("")
  let newblogs =[]
const focusInput = () => {
  const query = searchInput.current.value.toLowerCase();
  const filtered = blogs.filter(blog =>
    blog.title.toLowerCase().includes(query) ||
    blog.tags.toLowerCase().includes(query)
  );
  console.log(filtered); // هذه هي النتائج المطابقة
  // يمكن تحديث state لإعادة عرض النتائج
};


  return (
    <>
      <div>
        <div className="home">
        <h1>Discover Your Favorite Blogs</h1>
        
        <div className="search-box">
          <input 
            type="text" 
            placeholder="Search blog by title or tags..."
            name="search"
            ref={searchInput}
            onChange={focusInput}
            className="search"
          />
        </div>
        <div className="cards">
          <BlogCard blogs={blogs}/>
        </div>
      </div>
      </div>
    </>
  );
};

export default Home;
