import { createSlice } from "@reduxjs/toolkit";

const ListSlice = createSlice({
  name: "list",
  initialState: {
    list: [
      { title: "yafa", status: false },
      { title: "yara", status: false },
    ],
  },
  reducers: {
    insert: (state, action) => {
      state.list.push(action.payload); // ✅ Corrected
    },
    toComplete: (state, action) => {
        const index = action.payload;
        if (state.list[index]) {
          state.list[index].status = !state.list[index].status; // ✅ toggles true/false
        }
      }
      ,
    deleteItem:(state,action) =>{
        state.list.splice(action.payload, 1);
    }
  },
});

export const { insert, toComplete ,deleteItem } = ListSlice.actions;
export default ListSlice.reducer;
