import React from 'react'
import { useOutletContext } from 'react-router-dom'
function Show({id}) {
  const {movies} = useOutletContext()
  const datafilter =()=>{
    if(movies){
      return movies.find((el)=> el.id === id)
    }
  }
  const dataresult = datafilter()
  return (
    <div>
        <div
  className='show'
  style={{
  backgroundImage: `linear-gradient(to top, rgb(0, 0, 0), rgba(0, 0, 0, 0.98), rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.4)), url(${process.env.PUBLIC_URL}/images${dataresult.poster_path})`

  }}
>
  {dataresult?
    <>
      <div className='left-side' key={id}>
            <img src={`${process.env.PUBLIC_URL}/images${dataresult.poster_path}`} alt=''></img>
        </div>
        <div className='right-side'>
            <h1>{dataresult.original_title}</h1>
            <div className='types'>
                <div className='type'>Drama</div>
                <div className='type'>Crime</div>
            </div>
            <p>{dataresult.overview}</p>
            
        </div>
    </> : "there is no data!!"}
    </div>
    </div>
  )
}

export default Show
