import React, { useState } from "react";
import { useDispatch } from "react-redux";
function Form({list,selected}) {
  const [item,setItem] = useState({title:"" , status:false})
  const dispatch = useDispatch()
  const addList =(e) =>{
    e.preventDefault()
    dispatch(insert(item));
    setItem({ title: "", status: false }); // reset input after add

  }
  return (
    <form onSubmit={addList}>
      <div className="input">
        <input className="todo-input" value={item.title} onChange={(e) => setItem({ ...item, title: e.target.value })}/>
        <button className="todo-button">
          <i className="fas fa-plus-square"></i>
        </button>
      </div>

      <div className="select">
        <select name="todos" className="filter-todo" onChange={(e)=>selected(e.target.value)}>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="uncompleted">Uncompleted</option>
        </select>
      </div>
    </form>
  );
}

export default Form;
