import React, { useState } from 'react'
import { NavLink } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux';
import { logIn } from '../store/ProductSlice';
import { useNavigate } from "react-router-dom";
function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const {users} = useSelector((state) => state.product)
  const [info, setInfo] = useState({email: "", password: "" });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInfo((prev) => ({ ...prev, [name]: value }));
  };
  const handleSubmit = (e) => {
      e.preventDefault();
      const userChecks = users.find((el)=>{
        return el.email === info.email && el.password === info.password
      })
      if (userChecks) {
        console.log(userChecks)
      dispatch(logIn(userChecks.id));
      navigate("/"); // التوجيه بعد تسجيل الدخول
    } else {
      alert("Email or password incorrect");
    }
      setInfo({email: "", password: "" });
    };
  return (
    <div>
      <div className='about'>
        <h1 className="title">Login</h1>
        <hr/>
      </div>
      <form onSubmit={handleSubmit}>
        <label>Email address</label>
        <input type='email' placeholder='name@example.com' name="email" value={info.email} onChange={handleChange}></input>
        <label>Password</label>
        <input type='password' placeholder='Password' name="password" value={info.password} onChange={handleChange}></input>
        <p style={{"padding-top":"20px"}}>New Here? <a href="/register">Register</a></p>
        <button type="submit">login</button>
      </form>
    </div>
  )
}

export default Login
