import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const getMovies = createAsyncThunk('movietv/getMovies', async(_, thunkAPI) => {
    const {rejectWithValue} = thunkAPI
    try{
        const res = await fetch ("http://localhost:3005/movie")
        const data = await res.json()
        return data;

    }catch(error) {
        return rejectWithValue(error.message)
    }

})


const MovieSlice = createSlice({
  name: "movietv",
  initialState: {
    movies: [],
  },
  reducers: {
   
  },
  extraReducers: (builder) => {
    builder
      .addCase(getMovies.pending, (state) => {
        console.log("getBlogs pending")
      })
      .addCase(getMovies.fulfilled, (state, action) => {
        state.movies = action.payload;
        console.log("getBlogs fullfiled")
      })
      .addCase(getMovies.rejected, (state, action) => {
        console.log("getBlogs rejected")
      })
  }
});

export default MovieSlice.reducer;
