import "./App.css";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Outlet } from 'react-router-dom';
import { useSelector } from "react-redux";
import { useEffect } from "react";
import { fetchProduct , fetchUsers } from "./store/ProductSlice";
import { useDispatch } from "react-redux";
function App() {
  //state 
  const {products,filteredProducts ,cart} = useSelector((state) => state.product)
  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(fetchProduct());
    dispatch(fetchUsers());
  }, [dispatch]);

  return (
    <div className="App">
      <Navbar cart={cart}/>
      <div className="main">
        <Outlet context={{ products ,filteredProducts,cart}}/>
      </div>
      <Footer />
    </div>
  );
}

export default App;
