import React from "react";

function TodoItem({ items }) {
  return (
    <div className="todo">
      {items.map((el, index) => (
        <div key={index}>
          <h3>{el.title}</h3>
          <button className="complete-btn">
            <i className="fas fa-check"></i>
          </button>
          <button className="trash-btn">
            <i className="fas fa-trash"></i>
          </button>
        </div>
      ))}
    </div>
  );
}

export default TodoItem;
