import { createSlice } from "@reduxjs/toolkit";

const ListSlice = createSlice({
  name: "list",
  initialState: {
    list: [
      { title: "yafa", status: false },
      { title: "yara", status: false },
    ],
  },
  reducers: {
    insert: (state, action) => {
      state.list.push(action.payload); // ✅ Corrected
    },
    toComplete: (state, action) => {
      state.list[action.payload].status = !state.list[action.payload].status; // ✅ OK
      console.log(state.list)
    },
  },
});

export const { insert, toComplete } = ListSlice.actions;
export default ListSlice.reducer;
