import "./App.css";
import Form from "./components/Form";
import TodoList from "./components/TodoList";
import { useSelector }  from "react-redux";
function App() {
  const {list} = useSelector((state)=>state.list)
   const selected = (selectedStatus)=>{
    var selects = selectedStatus
    if (selects === "completed"){
      selects = true
    }
    if (selects === "uncompleted"){
      selects = false
    }
    console.log(selects)
  }
  return (
    <div className="App">
      <header>
        <h1>To-Do List</h1>
      </header>
      <Form list={list} selected={selected}/>
      <TodoList list={list} selected={selected}/>
    </div>
  );
}

export default App;
