import { useState } from 'react';
import { NavLink } from 'react-router-dom';

function Navbar({ size, resize }) {
  const [viewBar, setViewBar] = useState(false);

  return (
    <div
      className={`
        nav-container
        h-full
        text-white
        transition-all
        duration-300
        fixed md:relative
        top-0
        left-0
        z-50
        bg-gray-900
        flex flex-col
        ${viewBar ? 'w-[25%]' : 'w-[8%]'}
        md:w-[${size}%]
      `}
    >
      <nav className="flex flex-col h-full">
        {/* Toggle button */}
        <div className="flex justify-center md:justify-start p-4">
          <i
            className="fa-solid fa-bars text-3xl cursor-pointer"
            onClick={() => {
              resize(prev => (prev === 6 ? 25 : 6));
              setViewBar(!viewBar);
            }}
          ></i>
        </div>

        {/* Links */}
        <ul className="pages mt-7 flex flex-col w-full">
          <li className="mb-5 w-full">
            <NavLink
              to="/"
              className="flex items-center w-full py-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-house text-xl w-6 text-center"></i>
              {viewBar && <span className="ml-4">Home</span>}
            </NavLink>
          </li>
          <li className="mb-5 w-full">
            <NavLink
              to="movie"
              className="flex items-center w-full py-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-film text-xl w-6 text-center"></i>
              {viewBar && <span className="ml-4">Movies</span>}
            </NavLink>
          </li>
          <li className="mb-5 w-full">
            <NavLink
              to="Tv"
              className="flex items-center w-full py-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-tv text-xl w-6 text-center"></i>
              {viewBar && <span className="ml-4">Tv Series</span>}
            </NavLink>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
