import React from 'react'
import BlogCard from '../components/BlogCard'
import { useOutletContext, useParams } from 'react-router-dom';

function EditBlog() {
  const { id } = useParams();
  const { dispatch, blogs } = useOutletContext();
  const filteration = (id) => {
  const filterdata = blogs.filter((el) => el.id === +id);
  return filterdata;
};
const blogdata = filteration(id)
console.log(blogdata)
  return (
    <div>
      <div className='add-page'>
      <div className='title'>
        <h1>Edit Blog</h1>
      </div>
      {blogdata? <>
        <div className='add-blog'>
        <form>
          <input type='text' placeholder={blogdata.title} required ></input>
          <textarea className='input-2' placeholder='Blog Content' required></textarea>
          <input type='text' placeholder='Tags (comma separated)' required></input>
          <button type='submit' className='button'>Submit</button>
        </form>
      </div>
      </>: "There is no blog"}
    </div>
    </div>
  )
}

export default EditBlog
