// Card.jsx
import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink } from "react-router-dom";
const {series , movies} = useOutletContext()
function Card({toprated}) {
  const [swiperRef, setSwiperRef] = useState(null);
  const datafilter = () => {
    if (Array.isArray(toprated) && toprated.length > 0) {
      if (series && series.length > 0) {
        // ارجاع جميع العناصر التي تطابق IDs في toprated
        return series.filter((el) => toprated.includes(el.id));
      } else if (movies && movies.length > 0) {
        return movies.filter((el) => toprated.includes(el.id));
      }
    }
    return [];
  };
  const dataresult = datafilter()
  console.log(dataresult)

  return (
    <div className="Card">
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={6}
        spaceBetween={13}
        pagination={false}
        navigation={false}
        modules={[Pagination, Navigation]}
        className="mySwiper2"
      >
        {/* {toprated && toprated.length> 0 ? (toprated.map((el ,indx)=>{
          <SwiperSlide key={i} className="cardInfo">
            <NavLink className="imagehover" to="/"><img className="images" src={`${process.env.PUBLIC_URL}/images${el.poster_path}`}></img></NavLink>
            <h4>{el.}</h4>

          </SwiperSlide>
        })) :""} */}
      </Swiper>
    </div>
  );
}

export default Card;
