import React, { useState } from 'react'
import BlogCard from '../components/BlogCard'
import { useOutletContext, useParams } from 'react-router-dom';

function EditBlog() {
  const { id } = useParams();
  const { blogs } = useOutletContext();
  const [editData , setEditData]= useState({})
  const filteration = (id) => {
  const filterdata = blogs.find((el) => el.id === +id);
  return filterdata;
};
const EditHandler = (e)=>{
  e.preventDefault()
  console.log(e.target.value)
}
const blogdata = filteration(id)
  return (
    <div>
      <div className='add-page'>
      <div className='title'>
        <h1>Edit Blog</h1>
      </div>
      {blogdata? <>
        <div className='add-blog'>
        <form onSubmit={EditHandler}>
          <input type='text' name='title' placeholder="Blog Title" required value={blogdata.title} ></input>
          <textarea className='input-2' name='content' placeholder='Blog Content' value={blogdata.content}  required></textarea>
          <input type='text' name="tags" placeholder='Tags (comma separated)' value={blogdata.tags}  required></input>
          <button type='submit' className='button'>Submit</button>
        </form>
      </div>
      </>: "There is no blog"}
    </div>
    </div>
  )
}

export default EditBlog
