import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink } from "react-router-dom";
import { useSelector } from "react-redux";

function Card() {
  const { movies, series, toprated, trending } = useSelector((state) => state.movietv);
  const [swiperRef, setSwiperRef] = useState(null);

  // ðŸ§© Ø¯Ø§Ù„Ø© ÙÙ„ØªØ±Ø© Ù…Ø±Ù†Ø© ØªØªØ¹Ø§Ù…Ù„ Ù…Ø¹ Ø£Ù†ÙˆØ§Ø¹ IDs Ø§Ù„Ù…Ø®ØªÙ„ÙØ©
  const datafilter = (data, dataarray, type) => {
    if (!Array.isArray(dataarray) || dataarray.length === 0) return [];

    const top = dataarray[0];
    const idsKey = type === "series" ? "series_ids" : "movies_ids";
    const ids = top[idsKey];

    if (!Array.isArray(ids) || !Array.isArray(data)) return [];

    // âœ… Ù†Ø­ÙˆÙ„ ÙƒÙ„ Ø§Ù„Ù‚ÙŠÙ… Ø¥Ù„Ù‰ string Ù„Ù…Ø·Ø§Ø¨Ù‚Ø© Ø¯Ù‚ÙŠÙ‚Ø©
    const idsAsStrings = ids.map(String);

    return data.filter((el) => idsAsStrings.includes(String(el.id)));
  };

  // Ù†Ø¯Ù…Ø¬ Ø§Ù„Ù†ØªØ§Ø¦Ø¬ Ù…Ù† Ø§Ù„Ø³Ù„Ø§Ø³Ù„ ÙˆØ§Ù„Ø£ÙÙ„Ø§Ù…
  let dataresult = [];
  let types = "";

  if (toprated && toprated.length > 0) {
    const seriesData = datafilter(series, toprated, "series");
    const movieData = datafilter(movies, toprated, "movies");
    dataresult = [...seriesData, ...movieData];
    types = seriesData.length > 0 ? "tv" : "movie";
  } else if (trending && trending.length > 0) {
    const seriesData = datafilter(series, trending, "series");
    const movieData = datafilter(movies, trending, "movies");
    dataresult = [...seriesData, ...movieData];
    types = seriesData.length > 0 ? "tv" : "movie";
  }

  return (
    <div className="Card">
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={6}
        spaceBetween={13}
        pagination={false}
        navigation={false}
        modules={[Pagination, Navigation]}
        className="mySwiper2"
      >
        {dataresult.length > 0 ? (
          dataresult.map((el) => {
            const isSeries = !!el.original_name;
            const linkType = isSeries ? "tv" : "movie";
            return (
              <SwiperSlide key={el.id} className="cardInfo">
                <NavLink className="imagehover" to={`/${linkType}/${el.id}`}>
                  <img
                    className="images"
                    src={`${process.env.PUBLIC_URL}/images${el.poster_path}`}
                    alt={el.original_title || el.original_name}
                  />
                </NavLink>
                <h4>{el.original_title || el.original_name}</h4>
              </SwiperSlide>
            );
          })
        ) : (
          <p className="no-data">No matching items found</p>
        )}
      </Swiper>
    </div>
  );
}

export default Card;
