import { useState } from 'react';
import { NavLink } from 'react-router-dom';

function Navbar({ size, resize }) {
  const [viewBar, setViewBar] = useState(false);

  return (
    <div
      className={`
        nav-container 
        h-full 
        text-white 
        transition-all 
        duration-300 
        fixed md:relative 
        top-0 
        left-0 
        z-50 
        bg-gray-900
      `}
      style={{ width: `${size}%` }}
    >
      <nav className={`flex flex-col h-full ${viewBar ? 'items-start' : 'items-center'} transition-all`}>
        {/* Hamburger for small screens */}
        <div className="w-full flex justify-between items-center p-4 md:hidden">
          <i
            className="fa-solid fa-bars text-3xl cursor-pointer"
            onClick={() => {
              resize(prev => (prev === 6 ? 25 : 6));
              setViewBar(!viewBar);
            }}
          ></i>
          <span className="font-bold text-lg">Menu</span>
        </div>

        {/* Desktop toggle */}
        <i
          className="fa-solid fa-bars text-3xl cursor-pointer hidden md:block mt-4 ml-3"
          onClick={() => {
            resize(prev => (prev === 6 ? 25 : 6));
            setViewBar(!viewBar);
          }}
        ></i>

        {/* Links */}
        <ul className="pages mt-7 flex flex-col w-full">
          <li className="mb-5 w-full">
            <NavLink
              to="/"
              className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-house text-xl"></i>
              <span className={`${viewBar ? 'ml-5' : 'hidden'}`}>Home</span>
            </NavLink>
          </li>
          <li className="mb-5 w-full">
            <NavLink
              to="movie"
              className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-film text-xl"></i>
              <span className={`${viewBar ? 'ml-5' : 'hidden'}`}>Movies</span>
            </NavLink>
          </li>
          <li className="mb-5 w-full">
            <NavLink
              to="Tv"
              className="flex items-center w-full p-3 rounded-lg hover:bg-slate-950 transition"
            >
              <i className="fa-solid fa-tv text-xl"></i>
              <span className={`${viewBar ? 'ml-5' : 'hidden'}`}>Tv Series</span>
            </NavLink>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default Navbar;
