import React from "react";

function Form({list,selected}) {
  const addList =(e) =>{
    e.preventDefault()
  }
  return (
    <form onSubmit={addList}>
      <div className="input">
        <input className="todo-input"/>
        <button className="todo-button" onClick={(e)=>e.target}>
          <i className="fas fa-plus-square"></i>
        </button>
      </div>

      <div className="select">
        <select name="todos" className="filter-todo" onChange={(e)=>selected(e.target.value)}>
          <option value="all">All</option>
          <option value="completed">Completed</option>
          <option value="uncompleted">Uncompleted</option>
        </select>
      </div>
    </form>
  );
}

export default Form;
