import React from "react";
import { toComplete } from "../store/List";
import { useDispatch } from "react-redux";
function TodoItem({items}) {
  const dispatch = useDispatch()
  const checkHandler = (e,index) =>{
    dispatch(toComplete(index))
    console.log(e.target)
  }
  return (
    <>
    {items.map((el,indx)=>(
      <div className="todo" key={indx}>
      <li className={`${el.status==="completed" ? "completed":""}`}>{el.title}</li>
      <button className={`complete-btn`} onClick={(e)=>checkHandler(e,indx)}>
        <i  className={`"fas fa-check"${el.status==="completed" ? "completed":""}`}></i>
      </button>

      <button className="trash-btn">
        <i className="fas fa-trash"></i>
      </button>
      </div>
    ))}
    </>
  );
}

export default TodoItem;
