import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const getProducts = createAsyncThunk('product/getProducts', async(_, thunkAPI) => {
    const {rejectWithValue} = thunkAPI
    try{
        const res = await fetch ("http://localhost:3005/product")
        const data = await res.json()
        return data;

    }catch(error) {
        return rejectWithValue(error.message)
    }

})
export const getUsers = createAsyncThunk('product/getUsers', async(_, thunkAPI) => {
    const {rejectWithValue} = thunkAPI
    try{
        const res = await fetch ("http://localhost:3005/user")
        const data = await res.json()
        return data;

    }catch(error) {
        return rejectWithValue(error.message)
    }

})

const ProductSlice = createSlice({
  name: "product",
  initialState: {
    products: [],
    filteredProducts: [],
    cart: [],
    users:[],
    login:false,
  },
  reducers: {
    dataFilter: (state, action) => {
      if (action.payload === "All") {
        state.filteredProducts = state.products;
      } else {
        if(state.products){
          state.filteredProducts = state.products.filter(
          (el) => el.category === action.payload
        );
        }
      }
    },
    addToCart: (state, action) => {
      const product = state.products.find(el => el.id === action.payload);
      if (product) {
        const exist = state.cart.find(el => el.id === product.id);
        if (exist) {
          exist.quantity += 1;
        } else {
          state.cart.push({ ...product, quantity: 1 });
        }
      }
    },
    incrementQty: (state, action) => {
      const item = state.cart.find(el => el.id === action.payload);
      if (item) item.quantity += 1;
    },
    decrementQty: (state, action) => {
      const item = state.cart.find(el => el.id === action.payload);
      if (item && item.quantity > 1) item.quantity -= 1;
    },
    addUser:(state,action)=>{
      state.users.push(action.payload)
    },
    logIn :(state,action)=>{
      state.login = true
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(getProducts.pending, (state) => {
        console.log("pending")
      })
      .addCase(getProducts.fulfilled, (state, action) => {
        state.products = action.payload;
        state.filteredProducts = action.payload;
        console.log("fullfiled")
      })
      .addCase(getProducts.rejected, (state, action) => {
      })
      .addCase(getUsers.pending, (state, action) => {
        console.log("pending")
      })
      .addCase(getUsers.fulfilled, (state, action) => {
        state.users = action.payload;
        console.log("fullfiled")

      })
      .addCase(getUsers.rejected, (state, action) => {
        console.log("rejected")
      })
  }
});

export const { dataFilter, addToCart, incrementQty, decrementQty,addUser,logIn } = ProductSlice.actions;
export default ProductSlice.reducer;
