// Card.jsx
import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import { NavLink } from "react-router-dom";
function Card() {
  const [swiperRef, setSwiperRef] = useState(null);

  return (
    <div className="Card">
      <Swiper
        onSwiper={setSwiperRef}
        slidesPerView={6}
        spaceBetween={13}
        pagination={false}
        navigation={false}
        modules={[Pagination, Navigation]}
        className="mySwiper2"
      >
        {Array.from({ length: 7 }).map((_, i) => (
          <SwiperSlide key={i} className="cardInfo">
            <NavLink className="imagehover" to="/"><img className="images" src="https://image.tmdb.org/t/p/original//gqTz24ZRsCP6AKjARmEivY7m0cK.jpg"></img></NavLink>
            <h4>Titanic</h4>

          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default Card;
